#!/bin/bash

# +----------------------------------------------+
# | Script to run simulation and process results |
# +----------------------------------------------+

# Set output directory for measurement results
output_dir="../measurement"

# Do you want to delete the original .pcap file after conversion?
delete_pcap_after_conversion=True

# Do you want to disable TCP SACK?
disable_SACK=False

# Assign input parameters to meaningful variable names
cca=$1
predelay=$2
postdelay=$3
bandwidth=$4
buffer_size=$5
file_name=$6

# The dump file is generated by the simulation
pcap_file="$output_dir/test.pcap"

# Clean previous results
./clean.sh

# Create output directory if it doesn't exist
if [ ! -d "$output_dir" ]; then
    mkdir -p "$output_dir"
    echo "Folder '$output_dir' created."
else
    echo "Folder '$output_dir' already exists."
fi

# +-------------------------------------+
# | Run the simulation and capture data |
# +-------------------------------------+

# Disable TCP SACK if the parameter is True
if [ "$disable_SACK" == "True" ]; then
    sudo sh -c "echo '0' > /proc/sys/net/ipv4/tcp_sack"
fi

# Run the simulation
./simnet.sh "$cca" "$predelay" "$postdelay" "$bandwidth" "$buffer_size" "$file_name" "$pcap_file" "$output_dir"

# +-------------------------------------+
# | Convert received .pcap to .csv file |
# +-------------------------------------+

echo "Converting received data to .csv format"

# Create separate traces for TCP and UDP traffic
# Note: The output CSV for TCP traffic includes specific fields.
tshark -r "$pcap_file" -T fields \
    -o "gui.column.format:\"Time\",\"%Aut\"" \
    -e _ws.col.Time \
    -e frame.time_relative \
    -e tcp.time_relative \
    -e frame.number \
    -e frame.len \
    -e ip.src \
    -e tcp.srcport \
    -e ip.dst \
    -e tcp.dstport \
    -e tcp.len \
    -e tcp.seq \
    -e tcp.ack \
    -E header=y \
    -E separator=, \
    -E quote=d \
    -E occurrence=f >"$output_dir/$cca-tcp.csv"

# Capture UDP traffic
tshark -r "$pcap_file" -f "udp" -T fields -E header=y -E separator=, -E quote=d >"$output_dir/$cca-udp.csv"

# Delete PCAP file if the parameter is True
if [ "$delete_pcap_file" == "True" ]; then
    rm "$pcap_file"
fi

# Log the completion of the conversion
echo "Conversion completed: $output_dir/${pcap_file}-tcp.csv and $output_dir/${pcap_file}-udp.csv generated."

# Remove residual index files
# rm -f index*

# Completion message
echo "Test completed successfully."
